function getAbsUrl(pDir, relPath) {
    var root = pDir.split("/").slice(0, 3).join("/");

    if( relPath.startsWith(".") )
        return pDir + relPath.substring(1);
    else if( relPath.startsWith("/") )
        return root + relPath;
    else
        return pDir + "/" + relPath;
}

for(var t in wordLnData) {
    var topic = wordLnData[t];
    for(var i in topic) {
        var sd = topic[i].sound;
        topic[i].sound = getAbsUrl( "http://www.chekn.com/mate/audio", sd );
    }
}